import { getResponsiveProp, responsive } from "../helpers";
const BASE_STYLE = {
  minWidth: 0,
  minHeight: 0
};
function flexItemStyle() {
  return [BASE_STYLE, responsiveFlexItemStyle];
}
function responsiveFlexItemStyle(props) {
  const { theme } = props;
  const { media } = theme.sanity;
  return responsive(media, getResponsiveProp(props.$flex), (flex) => ({ flex }));
}
export {
  flexItemStyle,
  responsiveFlexItemStyle
};
