import { getResponsiveProp, rem, responsive } from "../../styles";
const BASE_STYLE = {
  width: "100%",
  margin: "0 auto"
};
function containerBaseStyle() {
  return BASE_STYLE;
}
function responsiveContainerWidthStyle(props) {
  const { theme } = props;
  const { container, media } = theme.sanity;
  return responsive(media, getResponsiveProp(props.$width), (val) => ({
    maxWidth: val === "auto" ? "none" : rem(container[val])
  }));
}
export {
  containerBaseStyle,
  responsiveContainerWidthStyle
};
