import { useEffect } from "react";
function useGlobalKeyDown(onKeyDown) {
  return useEffect(() => {
    addEventListener("keydown", onKeyDown);
    return () => removeEventListener("keydown", onKeyDown);
  }, [onKeyDown]);
}
export {
  useGlobalKeyDown
};
