import { getResponsiveProp, responsive } from "../helpers";
function responsiveTextAlignStyle(props) {
  const { theme } = props;
  return responsive(theme.sanity.media, getResponsiveProp(props.$align), (textAlign) => {
    return { textAlign };
  });
}
export {
  responsiveTextAlignStyle
};
