import { useMemo } from "react";
import { getResponsiveProp } from "../styles";
function useResponsiveProp(val, defaultVal) {
  return useMemo(() => getResponsiveProp(val, defaultVal), [defaultVal, val]);
}
export {
  useResponsiveProp
};
