import { useEffect, useState } from "react";
function useElementRect(element) {
  const [rect, setRect] = useState(null);
  useEffect(() => {
    if (!element)
      return void 0;
    const ro = new ResizeObserver((entries) => {
      setRect(entries[0].contentRect);
    });
    ro.observe(element);
    return () => ro.disconnect();
  }, [element]);
  return rect;
}
export {
  useElementRect
};
