import { getResponsiveProp, rem, responsive } from "../../styles";
const BASE_STYLE = {
  "&&:not([hidden])": {
    display: "grid"
  },
  '&[data-as="ul"],&[data-as="ol"]': {
    listStyle: "none"
  },
  gridTemplateColumns: "minmax(0, 1fr)",
  gridAutoRows: "min-content"
};
function stackBaseStyle() {
  return BASE_STYLE;
}
function responsiveStackSpaceStyle(props) {
  const { theme } = props;
  const { media, space } = theme.sanity;
  return responsive(media, getResponsiveProp(props.$space), (spaceIndex) => ({
    gridGap: rem(space[spaceIndex])
  }));
}
export {
  responsiveStackSpaceStyle,
  stackBaseStyle
};
