import { createContext } from "react";
import { globalScope } from "../../lib/globalScope";
const key = Symbol.for("@sanity/ui/context/layer");
globalScope[key] = globalScope[key] || createContext(null);
const LayerContext = globalScope[key];
export {
  LayerContext
};
