import { getResponsiveProp, responsive } from "../../styles";
function dialogStyle({ theme }) {
  const color = theme.sanity.color.base;
  return {
    "&:not([hidden])": {
      display: "flex"
    },
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    alignItems: "center",
    justifyContent: "center",
    outline: "none",
    background: color.shadow.penumbra
  };
}
function responsiveDialogPositionStyle(props) {
  const { theme } = props;
  const { media } = theme.sanity;
  return responsive(media, getResponsiveProp(props.$position), (position) => ({ "&&": { position } }));
}
export {
  dialogStyle,
  responsiveDialogPositionStyle
};
