import { getResponsiveProp, rem, responsive } from "../../styles";
function inlineBaseStyle() {
  return {
    lineHeight: 0,
    "&&:not([hidden])": {
      display: "block"
    },
    "& > div": {
      display: "inline-block",
      verticalAlign: "middle"
    }
  };
}
function inlineSpaceStyle(props) {
  const { theme } = props;
  return responsive(theme.sanity.media, getResponsiveProp(props.$space), (spaceIndex) => {
    const space = rem(theme.sanity.space[spaceIndex]);
    return {
      margin: `-${space} 0 0 -${space}`,
      "& > div": { padding: `${space} 0 0 ${space}` }
    };
  });
}
export {
  inlineBaseStyle,
  inlineSpaceStyle
};
